const wait=n=>new Promise((t=>window.setTimeout(t,n)));async function AutoType(n,t,e=80){const i="string"==typeof n?document.getElementById(n):n;i.innerHTML="&ZeroWidthSpace;";for(const n of t)i.innerHTML+=n,await wait(e)}async function AutoBackspace(n,t=50){const e="string"==typeof n?document.getElementById(n):n;for(const n of e.innerHTML)e.innerHTML=e.innerHTML.substring(0,e.innerHTML.length-1),e.innerHTML.length<=0&&(e.innerHTML="&ZeroWidthSpace;"),await wait(t)}